import styled from 'styled-components';
import { Container } from '../../GlobalStyles'

export const Work = styled.section`
  background-color: ${({theme}) => theme.colors.lightTeal}; 
  min-height: fit-content; 
  padding-top: 32px;
`

export const Wrapper = styled(Container)`
  padding: 32px;
  min-height: 950px;
  display: flex;
  flex-direction: column;
  align-items: center;
`

export const WorkContainer = styled.div`
  width: 100%;
  margin-top: 32px;
  display: flex;
  justify-content: center;
  padding: 16px;
  gap: 16px;
  flex-wrap: wrap;
  min-height: fit-content;
`

export const WorkCardContainer = styled.div`
  height: 420px; 
  width: 300px;
`

export const PreHoverContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  width: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  /* filter: blur(2px); */
  border-radius: 10px;
  box-shadow: inset 0 0 0 1000px rgba(249, 191, 59, .7);
`

export const PostHoverContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 100%;
  background-color: red;
  border-radius: 10px;
`
