import React, { useState } from 'react'
import { WorkCardContainer } from './MyWork.elements'
import { PreHoverContainer } from './MyWork.elements';

function WorkCard({image, title, description, links}) {

  const [hoverCard, setHoverCard] = useState(true);

  return (
    <WorkCardContainer>
      {hoverCard &&
      <PreHoverContainer style={{backgroundImage: `url(${image})`}}>

      </PreHoverContainer>
      }
    </WorkCardContainer>
    )
  
}

export default WorkCard
