import styled from 'styled-components';
import { Container, PageTitle } from '../../GlobalStyles'
import { motion } from 'framer-motion'

export const Work = styled.section`
  background-color: ${({theme}) => theme.colors.dark}; 
  min-height: 100vh; 
`

export const Wrapper = styled(Container)`
  padding: 120px 40px 40px 40px;
  display: flex; 
`

export const WorkPageTitle = styled(motion.h1)`
  height: 15%;
  color: ${({theme}) => theme.colors.lightTeal};
  font-size: 40px;
  font-weight: 500;
  flex: 1;
  position: sticky;
  top: 120px;
`

export const WorkContainer = styled.div`
  flex: 3;
  margin-left: 32px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 16px;
  gap: 64px;
  flex-wrap: wrap;
  min-height: fit-content;
`

export const WorkCardContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  justify-content: space-around;
  height: 560px;
  width: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  box-shadow: inset 0 0 0 1000px rgba(100, 100, 100, .6);
  padding: 24px;
`

export const WorkImage = styled.img`
  scale: .8;
  margin-top: 18px;
`

export const WorkTitle = styled.h1`
  text-decoration: underline;
  margin-top: 18px;
`

export const WorkDesc = styled.p`
  color: black;
  opacity: 110;
  width: 1/3
`

export const WorkTools = styled.ul`
  display: flex;
  justify-content: space-around;
  align-items: center;
  border: 1px solid red;
  list-style: none;
  gap: 12px;
`


export const WorkLinks = styled.div`
  display: flex;
  justify-content: space-around;
  width: 100%;
  margin-top: 18px;
`

export const WorkLink = styled.a`
  border: 2px solid black;
  padding: 10px 16px;
  text-decoration: none;
  background-color: lightgray;
  color: black;
  :hover {
    background-color: ${({theme}) => theme.colors.gray};
    cursor: pointer;
  }
`
