import React, { useEffect } from 'react'
import { WorkCardContainer, WorkDesc, WorkLinks, WorkLink, WorkTitle, WorkTools } from './MyWork.elements';
import { useAnimation } from 'framer-motion'
import { useInView } from 'react-intersection-observer'
import { workVariant } from '../../utils/variants'

function WorkCard({project}) {

  const control = useAnimation();
  const [ref, inView] = useInView();

  useEffect(() => {
    if(inView) control.start('visible');
  }, [control, inView]);

  return (
      <WorkCardContainer 
        style={{backgroundImage: `url(${project.image})`}}
        ref={ref}
        variants={workVariant}
        initial='hidden'
        animate={control}>
        <WorkTitle>{project.title}</WorkTitle>

        <WorkDesc>
          {project.description}
        </WorkDesc>

        <WorkTools>
          {project.builtWith.map((tool) => (
            <li>{tool}</li>
          ))}
        </WorkTools>

        <WorkLinks>
          <WorkLink href={project.siteLink} target='_blank'>Live Site</WorkLink>
          <WorkLink href={project.githubLink} target='_blank'>&#60; View Code &#62;</WorkLink>
        </WorkLinks>
      </WorkCardContainer>
    )
  
}



export default WorkCard
