import styled from 'styled-components'
import { Container, DefaultButton } from '../../GlobalStyles'

export const IntroPageCont = styled(Container)`
  height: calc(100vh);
  /* border: 1px solid blue; */
  display: flex;
  align-items: center;
  justify-content: center;
`

export const TitleCardCont = styled.div`
  max-height: 500px;
  height: 100%;
  max-width: 1000px;
  width: 100%;
  /* border: 1px solid red; */
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
`

export const IntroductionCont = styled.h1`
  color: ${({theme}) => theme.colors.dark};
  font-size: 58px;
  font-weight: 700;
  letter-spacing: 1px;
  /* border: 1px solid green; */
`

export const IntroStart = styled.p`
  font-size: 24px;
  font-weight: 300;
  color: ${({theme}) => theme.colors.lightDark};
  margin-bottom: 12px;
`

export const NameCont = styled.h1`
  color: ${({theme}) => theme.colors.dark};
  font-size: 64px;
  font-weight: 700;
  margin-bottom: 12px;
`

export const IntroEnd = styled.h2`
  font-size: 40px;
  color: ${({theme}) => theme.colors.lightDark};
  margin-bottom: 24px;
`

export const LinksCont = styled.div`
  height: 60px;
  /* border: 1px solid orange; */
  display: flex;
  align-items: center;
  padding: 0 24px;
  gap: 24px;
`

export const ResumeButton = styled(DefaultButton)`
  font-size: 16px;
  transition: .5s;
  &:hover {
    background-color: ${({theme}) => theme.colors.darkTeal};
    cursor: pointer;
  }
`
