export const variant = {
  hidden: { opacity: 0, y: '5vw' },
  visible: { 
    y: 0,
    opacity: 1,
    transition: {
      duration: 1
    } 
  }
};

export const workVariant = {
  hidden: { opacity: 0, x: '6vw' },
  visible: { 
    x: 0,
    opacity: 1,
    transition: {
      duration: 1.5
    } 
  }
}
