import styled from 'styled-components'
import { Container } from '../../GlobalStyles'


export const About = styled.section`
  background-color: ${({theme}) => theme.colors.lightTeal};  
`

export const AboutCont = styled(Container)`
  height: 950px;
  display: flex;
  flex-direction: column;
`

export const AboutTitle = styled.h1`
  width: 100%;
  height: 15%;
  display: flex;
  justify-content: center;
  align-items: center;
  color: ${({theme}) => theme.colors.dark};
  text-decoration: underline;
  font-size: 40px;
  font-weight: 300;
`

export const PICont = styled.div`
  height: 85%;
  width: 100%;
  display: flex;
`

export const MeCont = styled.div`
  width: 50%;
  height: 100%;
  margin: 0 24px;
`

export const MeTitle = styled.h2`
  height: 72px;
  display: flex;
  align-items: center;
  font-size: 32px;
  font-weight: 300;
  color: ${({theme}) => theme.colors.dark};
`
export const MeBody = styled.p`
  color: ${({theme}) => theme.colors.dark};
  margin: 48px 0;
`

export const ImageCont = styled.div`
  display: flex;
  justify-content: center;
  height: fit-content;
`

export const Image = styled.img`
  height: 500px;
`

export const SkillsCont = styled.div`
  width: 50%;
  height: 100%;
`
