import './App.css';
import { useEffect } from 'react';
import IntroPage from './Pages/IntroPage';
import AboutPage from './Pages/AboutPage';
import MyWork from './Pages/MyWork'
import Contact from './Pages/Contact';
import GlobalStyles, { GlobalContainer } from './GlobalStyles';
import { ThemeProvider } from 'styled-components';
import { useState } from 'react'
import { LocomotiveScrollProvider } from 'react-locomotive-scroll'
import { useRef } from 'react'
import LocomotiveScroll from 'locomotive-scroll';
import { Route, BrowserRouter as Router, Routes} from 'react-router-dom'

function App() {

  const lightTheme = {
    colors: {
      dark: '#0d1321',
      lightDark: '#1d2d44',
      gray: '#3e5c76',
      darkTeal: '#748cab',
      lightTeal: '#f0ebd8'
    }
  };

  const darkTheme = {
    colors :{
      dark: '#f0ebd8',
      lightDark: '#748cab',
      gray: '#3e5c76',
      darkTeal: '#1d2d44',
      lightTeal: '#0d1321'
    }
  };

  const [colorTheme, setColorTheme] = useState('dark');

  const containerRef = useRef(null);

  // useEffect(() => {
  //   let locomotiveScroll = new LocomotiveScroll({
  //     el: document.querySelector(".App"),
  //     smooth: true,
  //   });
  //   locomotiveScroll.update();
  //   window.scroll = locomotiveScroll;
  //   return () => {
  //     if (locomotiveScroll) locomotiveScroll.destroy();
  //   };
  // });

  return (
    <Router>
     <LocomotiveScrollProvider
      options={{smooth: true}}
      watch={[]}>
      <div data-scroll-container ref={containerRef} className="App">
        <ThemeProvider theme ={colorTheme === 'light' ? lightTheme : darkTheme}>

            <Routes>
              <Route path='/IntroPage' element={<IntroPage setColorTheme={setColorTheme}
                          colorTheme={colorTheme}
                          darkTheme={darkTheme}
                          lightTheme={lightTheme}/>} />

              <GlobalContainer>

                <Route path='/AboutPage' element={<AboutPage />}/>

                <Route path='/MyWork' element={<MyWork />}/>

                <Route path='/Contact' element={<Contact />}/>

              </GlobalContainer>
            </Routes>

          <GlobalStyles />


        </ThemeProvider>
      </div>
     </LocomotiveScrollProvider>
    </Router>
  );
}

export default App;
