import React, { useState } from 'react'
import { WorkCardContainer, PreHoverContainer, PostHoverContainer } from './MyWork.elements';

function WorkCard({image, logo, title, description, links}) {

  const [hoverCard, setHoverCard] = useState(false);

  return (
    <WorkCardContainer 
      onMouseEnter={() => setHoverCard(true)} 
      onMouseLeave={() => setHoverCard(false)}>

      {!hoverCard ?
      <PreHoverContainer style={{backgroundImage: `url(${image})`}}>
        <img src={logo} style={{scale: '.7'}}/>
      </PreHoverContainer>
      :
        <PostHoverContainer>
          
        </PostHoverContainer>
      }

    </WorkCardContainer>
    )
  
}

export default WorkCard
