import React from 'react'
import ParticleBackground from '../ParticleBackground'
import { IntroPageCont, TitleCardCont, IntroductionCont, LinksCont, NameCont, ResumeButton,
IntroStart, IntroEnd, IconButton, ThemeCont, SeeMoreButton } from '../Components/IntroPage/IntroPage.elements'
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import LightModeIcon from '@mui/icons-material/LightMode';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

function IntroPage({ colorTheme, setColorTheme, lightTheme, darkTheme }) {
  
const changeTheme = () => {
  const newTheme = colorTheme === 'light' ? 'dark' : 'light';
  setColorTheme(newTheme);
}

  return (
      <IntroPageCont id='IntroPage'>
        <ThemeCont>
          <IconButton onClick={changeTheme}>
            {colorTheme === 'dark' ? <LightModeIcon /> : <DarkModeIcon />}
          </IconButton> :
                                      
        </ThemeCont>
        <TitleCardCont>
          <IntroductionCont>
            <IntroStart>Hey, I'm</IntroStart>
            <NameCont>Aeron Horne,</NameCont>
            <IntroEnd>Software Developer.</IntroEnd>
          </IntroductionCont>
          <LinksCont>

            <IconButton><GitHubIcon /></IconButton>

            <IconButton><LinkedInIcon /></IconButton>

            <ResumeButton>Resume</ResumeButton>
            
          </LinksCont>
        </TitleCardCont>
        <SeeMoreButton  href='#AboutMe'><KeyboardArrowDownIcon/>See More<KeyboardArrowDownIcon/></SeeMoreButton>
        <ParticleBackground colorTheme={colorTheme === 'light' ? lightTheme : darkTheme}/>
      </IntroPageCont>
  )
}

export default IntroPage
