import React, { useState } from 'react'
import { WorkCardContainer } from './MyWork.elements'

function WorkCard({image, title, description, links}) {

  const [hoverCard, setHoverCard] = useState(false);

  return (
    <WorkCardContainer>
      {!hoverCard &&
      <div style={{backgroundImage: `url(${image})`}}>

      </div>
      }
    </WorkCardContainer>
    )
  
}

export default WorkCard
